import React, { useState } from 'react'
import { useRouter } from 'next/router'
import gql from 'graphql-tag'
import { useQuery } from '@apollo/react-hooks'
import withApollo from '../lib/withApollo'
import { Card, DataTable, Button, Page } from '@shopify/polaris'

const listTransactions = gql`
    query listTransactions {
        listTransactions {
            items {
                id
                totalPrice
                currency
                products {
                    id
                    title
                    bonusPercentage
                    priceAmount
                    priceCurrency
                    image
                }
                customer {
                    id
                    firstName
                    lastName
                    phone
                    email
                }
                createdAt
            }
        }
    }
`

const Index = () => {
    console.log('Hi there Zaven jan')
    const { loading, data, error } = useQuery(listTransactions)

    const router = useRouter()

    if (loading) {
        return <div>Loading...</div>
    }

    if (error) {
        console.log('Some error occured Zaven', error)
    }

    const rows = data.listTransactions.items.map((transaction) => [
        <Button>{transaction.id}</Button>,
        transaction.totalPrice,
        transaction.createdAt,
        transaction.customer[0].firstName
    ])

    console.log('Rows', rows)
    console.log('Here is transaction data Zvo', data)
    return (
        <Page title="Transactions">
            <Button
                primary
                onClick={() => router.push('https://dev.d7a3ao7d16ux1.amplifyapp.com/')}>
                Create transaction
            </Button>
            <Card>
                <DataTable
                    columnContentTypes={['text', 'text', 'text', 'text']}
                    headings={['OrderId', 'TotalPrice', 'CreatedAt', 'Customer']}
                    rows={rows}
                />
            </Card>
        </Page>
    )
}

export default withApollo(Index)
