import React, { useState, useCallback } from 'react'
import createApp from '@shopify/app-bridge'
import { Redirect } from '@shopify/app-bridge/actions'
import gql from 'graphql-tag'
import { useQuery } from '@apollo/react-hooks'
import withApollo from '../lib/withApollo'
import { Card, DataTable, Button, Page, TextField, ChoiceList, Filters } from '@shopify/polaris'
import { toCurrency } from '../utils/helper'

const listTransactions = gql`
    query listTransactions {
        listTransactions {
            items {
                id
                totalPrice
                totalBonusAmount
                currency
                products {
                    id
                    title
                    bonusPercentage
                    priceAmount
                    priceCurrency
                    image
                }
                customer {
                    id
                    firstName
                    lastName
                    phone
                    email
                }
                createdAt
            }
        }
    }
`

const Index = () => {
    console.log('Hi there Zaven jan')
    const [filterById, setFilterById] = useState(null)
    const [queryValue, setQueryValue] = useState(null)
    const { loading, data, error } = useQuery(listTransactions)

    const handleFilterByIdChange = useCallback((value) => setFilterById(value), [])

    const handleFiltersQueryChange = useCallback((value) => setQueryValue(value), [])

    const handleFilterByIdRemove = useCallback(() => setFilterById(null), [])

    const handleQueryValueRemove = useCallback(() => setQueryValue(null), [])

    const handleFiltersClearAll = useCallback(() => {
        handleFilterByIdRemove, handleQueryValueRemove
    }, [handleFilterByIdRemove, handleQueryValueRemove])

    const filters = [
        {
            key: 'filterById',
            label: 'FilterById',
            filter: (
                <ChoiceList
                    title="Filter by Id"
                    titleHidden
                    choices={[
                        { label: 'Order Id', value: 'Order Id' },
                        { label: 'Customer Id', value: 'Customer Id' }
                    ]}
                    selected={filterById || []}
                    onChange={handleFilterByIdChange}
                    allowMultiple
                />
            )
        }
    ]

    const app = createApp({
        apiKey: '3b01063bac3031d13101100ef3e44fd5',
        shopOrigin: 'transactions-avanta.myshopify.com'
    })

    const redirect = Redirect.create(app)
    if (loading) {
        return <div>Loading...</div>
    }

    if (error) {
        console.log('Some error occured Zaven', error)
    }

    const formatDate = (rowDate) => {
        const date = new Date(rowDate)
        const year = new Intl.DateTimeFormat('en', { year: 'numeric' }).format(date)
        const month = new Intl.DateTimeFormat('en', { month: 'short' }).format(date)
        const day = new Intl.DateTimeFormat('en', { day: '2-digit' }).format(date)

        return `${day}-${month}-${year}`
    }

    const rows = data.listTransactions.items.map((transaction) => [
        <Button
            onClick={() =>
                redirect.dispatch(Redirect.Action.ADMIN_PATH, {
                    path: `/orders/${transaction.id}?orderListBeta=true`,
                    newContext: true
                })
            }>
            {transaction.id}
        </Button>,
        toCurrency(Number(transaction.totalPrice)),
        toCurrency(Number(transaction.totalBonusAmount)),
        formatDate(transaction.createdAt),
        <Button
            primary
            onClick={() =>
                redirect.dispatch(Redirect.Action.ADMIN_PATH, {
                    path: `/customers/${transaction.customer[0].id}`,
                    newContext: true
                })
            }>
            {transaction.customer[0].email}
        </Button>
    ])

    const appliedFilters = []

    if (!isEmpty(filterById)) {
        const key = 'filterById'
        appliedFilters.push({
            key,
            label: disambiguateLabel(key, filterById),
            onRemove: handleFilterByIdRemove
        })
    }

    console.log('Rows', rows)
    console.log('Here is transaction data Zvo', data)
    return (
        <Page title="Transactions">
            <Button
                primary
                onClick={() =>
                    redirect.dispatch(Redirect.Action.REMOTE, {
                        url: 'https://dev.d3ivgpkzuz6hkr.amplifyapp.com',
                        newContext: true
                    })
                }>
                Create transaction
            </Button>
            <Card>
                <Card.Section>
                    <Filters
                        queryValue={queryValue}
                        filters={filters}
                        appliedFilters={appliedFilters}
                        onQueryChange={handleFiltersQueryChange}
                        onQueryClear={handleQueryValueRemove}
                        onClearAll={handleFiltersClearAll}
                    />
                </Card.Section>
                <DataTable
                    columnContentTypes={['text', 'text', 'text', 'text', 'text']}
                    headings={[
                        'OrderId',
                        'TotalPrice',
                        'TotalBonusAmount',
                        'CreatedAt',
                        'Customer'
                    ]}
                    rows={rows}
                />
            </Card>
        </Page>
    )

    function disambiguateLabel(key, value) {
        switch (key) {
            case 'taggedWith':
                return `Tagged with ${value}`
            case 'availability':
                return value.map((val) => `Available on ${val}`).join(', ')
            case 'productType':
                return value.join(', ')
            default:
                return value
        }
    }

    function isEmpty(value) {
        if (Array.isArray(value)) {
            return value.length === 0
        } else {
            return value === '' || value == null
        }
    }
}

export default withApollo(Index)
