import React from "react"
import { useQuery } from "react-query"
import { PaymentService } from "../services"

const useFetchPaymentRequest = (branchId, status, limit, nextToken) => {
  let paymentRequests

  if (nextToken !== "") {
    paymentRequests = useQuery(
      ["payment-request", { branchId, status, limit, nextToken }],
      PaymentService.fetchPaymentRequest,
      { enabled: false }
    )
    return paymentRequests
  }

  paymentRequests = useQuery(
    ["payment-request", { branchId, status, limit }],
    PaymentService.fetchPaymentRequest,
    { enabled: false }
  )
  return paymentRequests
}

