    //         })
    //     } catch (error) {
    //         console.log(error)
    //     }
    // }

    console.log('State data from product list', state)
    console.log('BranchInfo from modal', branchInfo)

    return (
        <Modal
            open={active}
            onClose={handleChange}
            title="Choose products"
            primaryAction={{
                content: 'Add products to branch',
                onAction: () => {
                    addProductsToBranch()
                    updateProducts()
                    handleChange()
                }
            }}>
            <Modal.Section>
                <Table celled striped selectable>
                    <Table.Header>
                        <Table.Row>
                            <Table.HeaderCell>Products</Table.HeaderCell>
                            <Table.HeaderCell textAlign="center">Price</Table.HeaderCell>
                            <Table.HeaderCell>Bonus Percentage</Table.HeaderCell>
                        </Table.Row>
                    </Table.Header>
                    <Table.Body>
                        {data &&
                            branchInfo &&
                            data.products.edges
                                // .filter((product) => {
                                //     if (searchValue == null) {
                                //         return product
                                //     } else if (
                                //         product.node.title
                                //             .toLowerCase()
                                //             .includes(searchValue.toLowerCase())
                                //     ) {
                                //         return product
                                //     }
                                // })
                                // .filter((product) =>
                                //     branchInfo.branchProducts.items
                                //         .map((product) => product.productId)
                                //         .includes(product.id)
                                // )
                                .map((product) => (
                                    <BranchRow
                                        product={product}
                                        state={state}
                                        setState={setState}
                                    />
