import React, { useState, useEffect, useRef, useCallback } from 'react'
import { Auth, API, graphqlOperation } from 'aws-amplify'
import gql from 'graphql-tag'
import { v4 as uuidv4 } from 'uuid'
import { Page, Button, Icon, Modal, Form, FormLayout, TextField } from '@shopify/polaris'
import { Table, Popup, Header } from 'semantic-ui-react'
import ProductList from './ProductsList'
import config from '../aws-exports'

API.configure(config)

const createBranch = gql`
    mutation createBranch($input: CreateBranchInput!) {
        createBranch(input: $input) {
            id
            adminId
            branchName
        }
    }
`

const BranchConsole = () => {
    const [active, setActive] = useState(false)
    const [branchName, setBranchName] = useState('')
    const [username, setUsername] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [code, setCode] = useState('')
    const [formType, setFormType] = useState('signUp')
    const [userSub, setUserSub] = useState(null)

    const onChangeBranchName = useCallback((newValue) => {
        setBranchName(newValue)
    }, [])

    const onChangeUsername = useCallback((newValue) => {
        setUsername(newValue)
    }, [])

    const onChangePassword = useCallback((newValue) => {
        setPassword(newValue)
    }, [])

    const onChangeEmail = useCallback((newValue) => {
        setEmail(newValue)
    }, [])

    const onChangeCode = useCallback((newValue) => {
        setCode(newValue)
    }, [])

    const handleChange = () => {
        setActive(!active)
    }

    const signUp = async (e) => {
        e.preventDefault()
        try {
            const user = await Auth.signUp({ username, password, attributes: { email } })
            setUsername(user.userSub)
            console.log('User from Modal', user)
            setFormType('confirm')
        } catch (error) {
            console.log(error)
        }
    }

    const confirm = async (e) => {
        e.preventDefault()
        try {
            const confirmedUser = await Auth.confirmSignUp(username, code)
            console.log('Confirmed user', confirmedUser)
            const branch = await API.graphql(
                graphqlOperation(createBranch, {
                    input: { adminId: userSub, branchName: branchName, id: uuidv4() }
                })
            )
            console.log('New created branch', branch)
            setFormType('signUp')
            handleChange()
        } catch (error) {
            console.log(error)
        }
    }

    console.log('Branch name', branchName)
    console.log('Username', username)
    console.log('Password', password)
    console.log('Email', email)
    console.log('Code', code)

    return (
        <>
            <Button onClick={handleChange} primary>
                Create branch
            </Button>
            <Table selectable celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Branch Name</Table.HeaderCell>
                        <Table.HeaderCell>Created At</Table.HeaderCell>
                        <Table.HeaderCell>Actions</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>
                {/* <Table.Body>
                    {paymentRequestItems &&
                        paymentRequestItems
                            .sort((a, b) => new Date(b.updatedAt) - new Date(a.updatedAt))
                            .map((item) => (
                                <Table.Row key={item.id}>
                                    <Table.Cell>
                                        <Badge size="small">{item.customerId}</Badge>
                                    </Table.Cell>
                                    <Table.Cell>{toCurrency(item.bonusAmount)}</Table.Cell>
                                    <Table.Cell>{formatDate(item.createdAt)}</Table.Cell>
                                    <Table.Cell>{formatDate(item.updatedAt)}</Table.Cell>
                                    <Table.Cell>
                                        <Badge
                                            size="small"
                                            progress="partiallyComplete"
                                            status="attention">
                                            {item.status}
                                        </Badge>
                                    </Table.Cell>
                                    <Table.Cell className="actions-cell">
                                        <Popup
                                            content="Approve payment"
                                            trigger={
                                                <Icon
                                                    className="accept"
                                                    name="check"
                                                    onClick={() => {
                                                        setPaymentRequestId(item.id)
                                                        handleChange()
                                                    }}
                                                />
                                            }
                                        />
                                        <Popup
                                            content="Decline payment"
                                            trigger={
                                                <Icon
                                                    className="decline"
                                                    name="remove"
                                                    onClick={async () => {
                                                        declinePayment(item.id)
                                                    }}
                                                />
                                            }
                                        />
                                    </Table.Cell>
                                </Table.Row>
                            ))}
                </Table.Body> */}
            </Table>
            <Modal
                open={active}
                onClose={handleChange}
                title="Fill in Branch info"
                //  primaryAction={{
                //      content: 'Proceed',
                //     //  onAction: () => {

                //     //  }
                //  }}
            >
                <Modal.Section>
                    {formType === 'signUp' && (
                        <Form>
                            <FormLayout>
                                <TextField
                                    value={branchName}
                                    onChange={onChangeBranchName}
                                    label="Branch Name"
                                />
                                <TextField
                                    value={username}
                                    onChange={onChangeUsername}
                                    label="Branch admin username"
                                />
                                <TextField
                                    value={email}
                                    onChange={onChangeEmail}
                                    label="Branch admin email"
                                />
                                <TextField
                                    type="password"
                                    value={password}
                                    onChange={onChangePassword}
                                    label="Branch admin password"
                                />
                                <Button onClick={signUp} primary submit>
                                    Create Branch
                                </Button>
                            </FormLayout>
                        </Form>
                    )}
                    {formType === 'confirm' && (
                        <Form>
                            <FormLayout>
                                <TextField
                                    value={username}
                                    onChange={onChangeUsername}
                                    label="Branch admin username"
                                />
                                <TextField
                                    value={code}
                                    onChange={onChangeCode}
                                    label="Confirmation code"
                                />
                                <Button onClick={confirm} primary submit>
                                    Confirm
                                </Button>
                            </FormLayout>
                        </Form>
                    )}
                </Modal.Section>
            </Modal>
        </>
    )
}

export default BranchConsole
