import React, { useState, useCallback, useEffect } from 'react'
import axios from 'axios'
import gql from 'graphql-tag'
import { print } from 'graphql'
import { API, graphqlOperation } from 'aws-amplify'
import { ResourcePicker } from '@shopify/app-bridge/actions'
import {
    Page,
    Badge,
    Avatar,
    TextStyle,
    Card,
    TextContainer,
    Modal,
    Checkbox,
    Thumbnail,
    Toast,
    Frame,
    Button
} from '@shopify/polaris'
import { CustomersMajorMonotone } from '@shopify/polaris-icons'
import { Table, TableCell, Icon, Popup, Header } from 'semantic-ui-react'
import { toCurrency, formatDate } from '../utils/helper'
import config from '../aws-exports'
import ProductList from '../components/ProductsList'

API.configure(config)

const listPaymentRequest = gql`
    query listPaymentRequest {
        listPaymentRequests {
            items {
                bonusAmount
                createdAt
                customerId
                id
                orderId
                status
                updatedAt
            }
        }
    }
`

const listProducts = gql`
    query Products {
        products(first: 100) {
            edges {
                node {
                    id
                    title
                    tags
                    description(truncateAt: 100)
                    variants(first: 1) {
                        edges {
                            node {
                                id
                                price
                            }
                        }
                    }
                    images(first: 1) {
                        edges {
                            node {
                                originalSrc
                            }
                        }
                    }
                }
            }
        }
    }
`

const createDraftOrder = gql`
    mutation CreateOrder($input: DraftOrderInput!) {
        draftOrderCreate(input: $input) {
            draftOrder {
                id
                customer {
                    id
                }
                email
                lineItems(first: 10) {
                    edges {
                        node {
                            title
                            quantity
                            originalUnitPrice
                            variant {
                                id
                                price
                                title
                                product {
                                    tags
                                    title
                                    images(first: 5) {
                                        edges {
                                            node {
                                                originalSrc
                                            }
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }
`

const completeOrder = gql`
    mutation CompleteOrder($id: ID!) {
        draftOrderComplete(id: $id, paymentPending: true) {
            draftOrder {
                customer {
                    id
                }
                email
                name
            }
        }
    }
`

const AcceptPayment = ({ paymentRequests, products }) => {
    const [paymentRequestItems, setPaymentRequestItems] = useState(
        paymentRequests.listPaymentRequests.items
    )
    const [productItems, setProductItems] = useState(products)
    const [openPicker, setOpenPicker] = useState(false)
    const [active, setActive] = useState(false)
    const [rowId, setRowId] = useState([])
    const [state, setState] = useState({
        customerId: '',
        bonusAmount: '',
        products: []
    })
    const [searchValue, setSearchValue] = useState(null)
    const [activeToast, setActiveToast] = useState(false)
    const handleSearchInput = useCallback((newValue) => setSearchValue(newValue), [])

    const handleChange = useCallback(() => setActive(!active), [active])

    const createOrder = async () => {
        try {
            const products = await axios({
                url: '/',
                method: 'post',
                headers: {
                    'Content-Type': 'application/json',
                    'X-Shopify-Access-Token': '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'
                },
                data: {
                    query: print(listProducts)
                }
            })

            console.log('products from axios', products.data)
            // const orderRes = await axios({
            //     url: 'https://transactions-avanta.myshopify.com/admin/api/2020-07/graphql.json',
            //     method: 'post',
            //     headers: {
            //         'Content-Type': 'application/json',
            //         'X-Shopify-Access-Token': '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'
            //     },
            //     data: {
            //         query: print(createDraftOrder),
            //         variables: {
            //             input: {
            //                 customerId: `gid://shopify/Customer/${state.customerId}`,
            //                 taxExempt: true,
            //                 note: `${toCurrency(
            //                     state.bonusAmount
            //                 )} of bonus were spent on these products `,
            //                 lineItems: state.products.map((product) => ({
            //                     quantity: 1,
            //                     originalUnitPrice: Number(
            //                         product.node.variants.edges[0].node.price
            //                     ),
            //                     variantId: product.node.variants.edges[0].node.id
            //                 }))
            //             }
            //         }
            //     }
            // })

            // const order = orderRes.data

            // console.log('Order from createOrder function', order)

            // const sendCompleteOrder = await axios({
            //     url: 'https://transactions-avanta.myshopify.com/admin/api/2020-07/graphql.json',
            //     method: 'post',
            //     headers: {
            //         'Content-Type': 'application/json',
            //         'X-Shopify-Access-Token': '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'
            //     },
            //     data: {
            //         query: print(completeOrder),
            //         variables: {
            //             id: order.data.draftOrderCreate.draftOrder.id
            //         }
            //     }
            // })

            // console.log('Order created Zav', sendCompleteOrder.data)
        } catch (error) {
            console.log(error)
        }
    }

    const toastMarkup = activeToast ? (
        <Toast
            content="Order has been successfully created"
            onDismiss={() => setActiveToast(false)}
        />
    ) : null

    console.log('Payment requests from getStaticProps', paymentRequests)
    console.log('products data', products)
    console.log('Payment request items', paymentRequestItems)
    console.log('Products from modal', state.products)
    console.log('Customer id', state.customerId)
    return (
        <Page title="Payment Request">
            <Button onClick={createOrder}>Get Products</Button>
            <Table selectable celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Customer Id</Table.HeaderCell>
                        <Table.HeaderCell>Order Id</Table.HeaderCell>
                        <Table.HeaderCell>Bonus Amount</Table.HeaderCell>
                        <Table.HeaderCell>Created At</Table.HeaderCell>
                        <Table.HeaderCell>Updated At</Table.HeaderCell>
                        <Table.HeaderCell>Status</Table.HeaderCell>
                        <Table.HeaderCell>Action</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>
                <Table.Body>
                    {paymentRequestItems &&
                        paymentRequestItems.map((item) => (
                            <Table.Row key={item.id}>
                                <Table.Cell>
                                    <Badge size="small">{item.customerId}</Badge>
                                </Table.Cell>
                                <Table.Cell>
                                    <Badge size="small">{item.orderId}</Badge>
                                </Table.Cell>
                                <Table.Cell>{toCurrency(item.bonusAmount)}</Table.Cell>
                                <Table.Cell>{formatDate(item.createdAt)}</Table.Cell>
                                <Table.Cell>{formatDate(item.updatedAt)}</Table.Cell>
                                <Table.Cell>
                                    <Badge
                                        size="small"
                                        status={
                                            item.status === 'PENDING'
                                                ? 'attention'
                                                : item.status === 'APPROVED'
                                                ? 'success'
                                                : 'critical'
                                        }>
                                        {item.status}
                                    </Badge>
                                </Table.Cell>
                                <Table.Cell className="actions-cell">
                                    <Popup
                                        content="Approve payment"
                                        trigger={
                                            <Icon
                                                className="accept"
                                                name="check"
                                                onClick={() => {
                                                    setState({
                                                        ...state,
                                                        customerId: item.customerId,
                                                        bonusAmount: item.bonusAmount
                                                    })
                                                    handleChange()
                                                }}
                                            />
                                        }
                                    />
                                    <Popup
                                        content="Decline payment"
                                        trigger={<Icon className="decline" name="remove" />}
                                    />
                                </Table.Cell>
                            </Table.Row>
                        ))}
                </Table.Body>
            </Table>
            <ProductList active={active} handleChange={handleChange} />
            {/* <div style={{ height: '500px' }}>
                <Modal
                    open={active}
                    onClose={handleChange}
                    title="Choose products"
                    primaryAction={{
                        content: 'Proceed',
                        onAction: () => {
                            createOrder()
                            handleChange()
                            setActiveToast(true)
                        }
                    }}>
                    <Modal.Section>
                        <Table celled striped selectable>
                            <Table.Header>
                                <Table.Row>
                                    <Table.HeaderCell>Products</Table.HeaderCell>
                                    <Table.HeaderCell>Description</Table.HeaderCell>
                                    <Table.HeaderCell textAlign="center">Price</Table.HeaderCell>
                                </Table.Row>
                            </Table.Header>
                            <Table.Body>
                                {productItems &&
                                    productItems
                                        .filter((product) => {
                                            if (searchValue == null) {
                                                return product
                                            } else if (
                                                product.node.title
                                                    .toLowerCase()
                                                    .includes(searchValue.toLowerCase())
                                            ) {
                                                return product
                                            }
                                        })
                                        .map((product) => (
                                            <Table.Row
                                                key={product.node.id}
                                                onClick={() => {
                                                    if (rowId.includes(product.node.id)) {
                                                        setRowId(
                                                            rowId.filter(
                                                                (id) => id !== product.node.id
                                                            )
                                                        )
                                                        setState({
                                                            products: state.products.filter(
                                                                (filteredProduct) =>
                                                                    filteredProduct.node.id !==
                                                                    product.node.id
                                                            )
                                                        })
                                                    } else {
                                                        setRowId([...rowId, product.node.id])
                                                        setState({
                                                            products: [...state.products, product]
                                                        })
                                                    }
                                                }}>
                                                <Table.Cell>
                                                    <Header
                                                        as="h4"
                                                        image
                                                        className="product-header">
                                                        <Checkbox
                                                            checked={
                                                                rowId.includes(product.node.id) &&
                                                                true
                                                            }
                                                        />
                                                        <Thumbnail
                                                            source={
                                                                product.node.images.edges[0] &&
                                                                product.node.images.edges[0].node
                                                                    .originalSrc
                                                            }
                                                            size="small"
                                                            alt=""
                                                        />
                                                        <Header.Content>
                                                            {product.node.title}
                                                        </Header.Content>
                                                    </Header>
                                                </Table.Cell>
                                                <Table.Cell>
                                                    <TextStyle variation="subdued">
                                                        {product.node.description}
                                                    </TextStyle>
                                                </Table.Cell>
                                                <Table.Cell textAlign="center">
                                                    <TextStyle variation="subdued">
                                                        {product.node.variants.edges[0] &&
                                                            toCurrency(
                                                                product.node.variants.edges[0].node
                                                                    .price
                                                            )}
                                                    </TextStyle>
                                                </Table.Cell>
                                            </Table.Row>
                                        ))}
                            </Table.Body>
                            <Table.Footer fullWidth>
                                <Table.Row></Table.Row>
                            </Table.Footer>
                        </Table>
                    </Modal.Section>
                </Modal>
            </div> */}
            {toastMarkup}
        </Page>
    )
}

export async function getStaticProps() {
    let result
    try {
        result = await API.graphql(graphqlOperation(listPaymentRequest))
    } catch (error) {
        console.log(error)
    }

    if (result.errors) {
        console.warn('Failed to fetch payment requests.', result.errors)
        return { props: { paymentRequests: [] } }
    }
    if (result.data !== null) {
        return {
            props: { paymentRequests: result.data }
        }
    }
}

export default AcceptPayment
