import React, { useState, useCallback } from 'react'
import axios from 'axios'
import { API, graphqlOperation } from 'aws-amplify'
import gql from 'graphql-tag'
import { useQuery } from '@apollo/client'
import { Modal, Thumbnail, Checkbox, TextStyle } from '@shopify/polaris'
import { Table, Header } from 'semantic-ui-react'
import { toCurrency, formatDate } from '../utils/helper'
import config from '../aws-exports'

const BranchRow = ({ product, state, setState }) => {
    const [fetchedProduct, setFetchedProduct] = useState(product)
    const [rowId, setRowId] = useState([])
    const [searchValue, setSearchValue] = useState(null)
    const [bonus, setBonus] = useState('')
    const handleSearchInput = useCallback((newValue) => setSearchValue(newValue), [])

    const handleBonus = useCallback((value) => {
        setBonus(value)
        fetchedProduct.node.tags = [value]
        setFetchedProduct(fetchedProduct)
    }, [])

    return (
        <Table.Row key={product.node.id}>
            <Table.Cell>
                <Header as="h4" image className="product-header">
                    <Checkbox
                        checked={rowId.includes(product.node.id) && true}
                        onClick={() => {
                            if (rowId.includes(product.node.id)) {
                                setRowId(rowId.filter((id) => id !== product.node.id))
                                setState({
                                    products: state.products.filter(
                                        (filteredProduct) =>
                                            filteredProduct.node.id !== product.node.id
                                    )
                                })
                            } else {
                                setRowId([...rowId, product.node.id])
                                setState({
                                    ...state,
                                    products: [...state.products, product]
                                })
                            }
                        }}
                    />
                    <Thumbnail
                        source={
                            product.node.images.edges[0] &&
                            product.node.images.edges[0].node.originalSrc
                        }
                        size="small"
                        alt=""
                    />
                    <Header.Content>{product.node.title}</Header.Content>
                </Header>
            </Table.Cell>
            <Table.Cell textAlign="center">
                <TextStyle variation="subdued">
                    {product.node.variants.edges[0] &&
                        toCurrency(product.node.variants.edges[0].node.price)}
                </TextStyle>
            </Table.Cell>
            <Table.Cell>
                <TextField
                    id="percentage-input"
                    placeholder="Bonus Percentage"
                    value={bonus}
                    onChange={handleBonus}
                    prefix={<Icon name="percent" />}
                    disabled={!rowId.includes(product.node.id)}
                />
            </Table.Cell>
        </Table.Row>
    )
}

export default BranchRow
