import React, { useState } from 'react'
import { Auth } from 'aws-amplify'

const Branch = () => {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        code: '',
        formType: 'signUp'
    })

    const onChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value })
    }

    const signUp = async (e) => {
        e.preventDefault()
        const { username, email, password } = formData
        try {
            const { user } = await Auth.signUp({ username, password, attributes: { email } })

            console.log(user)
            setFormData({ ...formData, formType: 'confirm' })
        } catch (error) {
            console.log(error)
        }
    }

    const confirm = async (e) => {
        e.preventDefault()
        const { username, code } = formData
        try {
            await Auth.confirmSignUp(username, code)
            setFormData({ ...formData, formType: 'signIn' })
        } catch (error) {
            console.log(error)
        }
    }

    const signIn = async (e) => {
        e.preventDefault()
        const { username, password } = formData
        try {
            const user = Auth.signIn(username, password)
            console.log('User: user')
        } catch (error) {
            console.log(error)
        }
    }

    const { username, email, password, code, formType } = formData

    return (
        <div>
            {formType === 'signUp' && (
                <form onSubmit={signUp}>
                    <input
                        name="username"
                        value={username}
                        type="text"
                        placeholder="username"
                        onChange={onChange}
                    />
                    <input
                        name="password"
                        value={password}
                        type="password"
                        placeholder="password"
                        onChange={onChange}
                    />
                    <input
                        name="email"
                        value={email}
                        type="text"
                        placeholder="email"
                        onChange={onChange}
                    />
                    <button type="submit">Submit</button>
                </form>
            )}
            {formType === 'signIn' && (
                <form onSubmit={signIn}>
                    <input
                        name="username"
                        value={username}
                        type="text"
                        placeholder="username"
                        onChange={onChange}
                    />
                    <input
                        name="password"
                        value={password}
                        type="password"
                        placeholder="password"
                        onChange={onChange}
                    />
                    <button type="submit">Submit</button>
                </form>
            )}
            {formType === 'confirm' && (
                <form onSubmit={confirm}>
                    <input
                        name="username"
                        value={username}
                        type="text"
                        placeholder="username"
                        onChange={onChange}
                    />
                    <input
                        name="code"
                        value={code}
                        type="text"
                        placeholder="confirm code"
                        onChange={onChange}
                    />
                    <button type="submit">Submit</button>
                </form>
            )}
        </div>
    )
}

export default Branch
