import { API, graphqlOperation } from "aws-amplify"
import config from "../../aws-exports"
import { listPaymentRequest } from "../../graphql/queries"
import { updatePaymentRequest } from "../../graphql/mutation"

API.configure(config)

export const fetchPaymentRequest = ({ branchId, status, limit, nextToken }) => {
  return API.graphql(graphqlOperation(listPaymentRequest, { branchId, status, limit, nextToken }))
}

export const updatePayment = ({ paymentId, status, products }) => {
  return API.graphql(
    graphqlOperation(updatePaymentRequest, {
      input: {
        id: paymentId,
        status,
        products: products ? products : "",
      },
    })
  )
}
