import React, { useState, useEffect, useCallback } from "react"
import { API, graphqlOperation } from "aws-amplify"
import { Table } from "semantic-ui-react"
import { Badge, Button } from "@shopify/polaris"
import { useFetchPaymentRequest } from "../core/hooks"
import { toCurrency, formatDate } from "../utils/helper"
import config from "../aws-exports"
import { listPaymentRequest } from "../graphql/queries"

API.configure(config)

const AcceptedPaymentRequest = ({ branchId }) => {
  const [nextPaginateToken, setNextPaginateToken] = useState("")
  const [paymentRequestItems, setPaymentRequestItems] = useState([])
  const { data: paymentRequests, refetch: getPaymentRequests } = useFetchPaymentRequest(
    branchId,
    "APPROVED",
    3,
    nextPaginateToken
  )

  // const fetchAcceptedPayments = useCallback(() => {
  //   try {
  //     fetchPaymentRequest({ branchId, status: "APPROVED", limit: 3 })
  //     setNextPaginateToken(data.data.listPaymentRequests.nextToken)
  //     setPaymentRequestItems(data.data.listPaymentRequests.items)
  //   } catch (error) {
  //     console.log(error)
  //   }
  // }, [])

  useEffect(() => {
    getPaymentRequests()
  }, [branchId, getPaymentRequests])

  useEffect(() => {
    setNextPaginateToken(paymentRequests && paymentRequests.data.listPaymentRequests.nextToken)
    setPaymentRequestItems(paymentRequests && paymentRequests.data.listPaymentRequests.items)
  }, [paymentRequests])

  console.log("Payment requests from useEffect", paymentRequests)

  console.log("Next paginate token", nextPaginateToken)

  // const fetchAcceptedPayments = useCallback(async () => {
  //   try {
  //     const res = await API.graphql(
  //       graphqlOperation(listPaymentRequest, { limit: 3, branchId, status: "APPROVED" })
  //     )
  //     setNextPaginateToken(res.data.listPaymentRequests.nextToken)
  //     setPaymentRequestItems(res.data.listPaymentRequests.items)
  //   } catch (error) {
  //     console.log(error)
  //   }
  // }, [])

  // useEffect(() => {
  //   fetchAcceptedPayments()
  // }, [fetchAcceptedPayments])

  const loadMore = () => {
    try {
      getPaymentRequests
      setNextPaginateToken(paymentRequests && paymentRequests.data.listPaymentRequests.nextToken)
      setPaymentRequestItems([
        ...paymentRequestItems,
        ...paymentRequests.data.listPaymentRequests.items,
      ])
    } catch (error) {
      console.log(error)
    }
  }

  // const loadMore = async () => {
  //   try {
  //     const res = await API.graphql(
  //       graphqlOperation(listPaymentRequest, {
  //         limit: 3,
  //         nextToken: nextPaginateToken,
  //         branchId,
  //         status: "APPROVED",
  //       })
  //     )
  //     setNextPaginateToken(res.data.listPaymentRequests.nextToken)
  //     setPaymentRequestItems([...paymentRequestItems, ...res.data.listPaymentRequests.items])
  //   } catch (error) {
  //     console.log(error)
  //   }
  // }

  return (
    <>
      <Table celled>
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell>Customer Id</Table.HeaderCell>
            <Table.HeaderCell>Order Id</Table.HeaderCell>
            <Table.HeaderCell>Bonus Amount</Table.HeaderCell>
            <Table.HeaderCell>Created At</Table.HeaderCell>
            <Table.HeaderCell>Updated At</Table.HeaderCell>
            <Table.HeaderCell>Status</Table.HeaderCell>
          </Table.Row>
        </Table.Header>
        <Table.Body>
          {paymentRequestItems &&
            paymentRequestItems
              .sort((a, b) => new Date(b.updatedAt) - new Date(a.updatedAt))
              .map((item) => (
                <Table.Row key={item.id}>
                  <Table.Cell>
                    <Badge size="small">{item.customerId}</Badge>
                  </Table.Cell>
                  <Table.Cell>
                    <Badge size="small">{item.orderId}</Badge>
                  </Table.Cell>
                  <Table.Cell>{toCurrency(item.bonusAmount)}</Table.Cell>
                  <Table.Cell>{formatDate(item.createdAt)}</Table.Cell>
                  <Table.Cell>{formatDate(item.updatedAt)}</Table.Cell>
                  <Table.Cell>
                    <Badge size="small" progress="complete" status="success">
                      {item.status}
                    </Badge>
                  </Table.Cell>
                </Table.Row>
              ))}
        </Table.Body>
      </Table>
      <Button disabled={!nextPaginateToken} primary onClick={loadMore}>
        Load more
      </Button>
    </>
  )
}

export default AcceptedPaymentRequest
