import { useState, useCallback } from "react"
import { Card, Button, TextField, Form, FormLayout } from "@shopify/polaris"
import { useMutation } from "react-query"
import { AuthService } from "../core/services"
import Amplify from "aws-amplify"
import { useForm } from "react-hook-form"
import { useLogin } from "../core/hooks"
import config from "../aws-exports"

Amplify.configure(config)

const Profile = ({ setUser }) => {
  const [username, setUsername] = useState("")
  const [password, setPassword] = useState("")

  const [login, { status, data, error }] = useMutation(AuthService.login)
  // const { handleSubmit, errors, register } = useForm()
  // const { login, isLoading, data } = useLogin()

  const onChangeUsername = useCallback((newValue) => {
    setUsername(newValue)
  }, [])

  const onChangePassword = useCallback((newValue) => {
    setPassword(newValue)
  }, [])

  const handleSubmit = async (e) => {
    e.preventDefault()
    await login(username, password)
  }

  console.log("Data from useLogin hook", data)
  console.log("Username", username)
  console.log("Password", password)

  return (
    <div className="authentication-container">
      <Card title="Log into your branch" sectioned>
        <Form onSubmit={handleSubmit}>
          <FormLayout>
            {/* <input
              name="username"
              ref={register({ required: "Username is required!" })}
              placeholder="Username"
            />
            {errors.username && <p>{errors.username.message}</p>}
            <input
              name="password"
              ref={register({ required: "Password is required!" })}
              placeholder="Password"
              type="password"
            />
            {errors.password && <p>{errors.password.message}</p>} */}
            <TextField value={username} onChange={onChangeUsername} label="Username" />
            <TextField
              type="password"
              value={password}
              onChange={onChangePassword}
              label="Password"
            />
            <Button primary submit>
              Login
            </Button>
          </FormLayout>
        </Form>
      </Card>
    </div>
  )
}

export default Profile
