import React, { useState, useCallback } from 'react'
import gql from 'graphql-tag'
import { API, graphqlOperation } from 'aws-amplify'
import { Table, Icon, Popup, Header } from 'semantic-ui-react'
import { Badge } from '@shopify/polaris'
import { toCurrency, formatDate } from '../utils/helper'
import ProductList from './ProductsList'
import config from '../aws-exports'

API.configure(config)

const updatePaymentRequest = gql`
    mutation updatePaymentRequest($input: UpdatePaymentRequestInput!) {
        updatePaymentRequest(input: $input) {
            bonusAmount
            customerId
            id
            orderId
            status
            products {
                originalUnitPrice
                quantity
                variantId
            }
            createdAt
            updatedAt
        }
    }
`

const listPaymentRequest = gql`
    query listPaymentRequest {
        listPaymentRequests {
            items {
                bonusAmount
                createdAt
                customerId
                id
                orderId
                status
                updatedAt
            }
            nextToken
        }
    }
`

const AcceptedPaymentRequest = ({ paymentRequests }) => {
    const [paymentRequestId, setPaymentRequestId] = useState('')
    const [active, setActive] = useState(false)

    const handleChange = useCallback(() => setActive(!active), [active])

    console.log('Payment request id for decline', paymentRequestId)
    console.log('Accepted payments', paymentRequests)

    const declinePayment = async (paymentId) => {
        try {
            const paymentResponse = await API.graphql(
                graphqlOperation(updatePaymentRequest, {
                    input: {
                        id: paymentId,
                        status: 'DECLINED'
                    }
                })
            )

            console.log('Declined Payment data from lambda', paymentResponse.data)
        } catch (error) {
            console.log(error)
        }
    }
    return (
        <>
            <Table selectable celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Customer Id</Table.HeaderCell>
                        <Table.HeaderCell>Bonus Amount</Table.HeaderCell>
                        <Table.HeaderCell>Created At</Table.HeaderCell>
                        <Table.HeaderCell>Updated At</Table.HeaderCell>
                        <Table.HeaderCell>Status</Table.HeaderCell>
                        <Table.HeaderCell>Action</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>
                {/* <Table.Body>
                    {paymentRequestItems &&
                        paymentRequestItems
                            .filter((item) => item.status === 'PENDING')
                            .map((item) => (
                                <Table.Row key={item.id}>
                                    <Table.Cell>
                                        <Badge size="small">{item.customerId}</Badge>
                                    </Table.Cell>
                                    <Table.Cell>{toCurrency(item.bonusAmount)}</Table.Cell>
                                    <Table.Cell>{formatDate(item.createdAt)}</Table.Cell>
                                    <Table.Cell>{formatDate(item.updatedAt)}</Table.Cell>
                                    <Table.Cell>
                                        <Badge
                                            size="small"
                                            progress="partiallyComplete"
                                            status="attention">
                                            {item.status}
                                        </Badge>
                                    </Table.Cell>
                                    <Table.Cell className="actions-cell">
                                        <Popup
                                            content="Approve payment"
                                            trigger={
                                                <Icon
                                                    className="accept"
                                                    name="check"
                                                    onClick={() => {
                                                        setPaymentRequestId(item.id)
                                                        handleChange()
                                                    }}
                                                />
                                            }
                                        />
                                        <Popup
                                            content="Decline payment"
                                            trigger={
                                                <Icon
                                                    className="decline"
                                                    name="remove"
                                                    onClick={async () => {
                                                        declinePayment(item.id)
                                                    }}
                                                />
                                            }
                                        />
                                    </Table.Cell>
                                </Table.Row>
                            ))}
                </Table.Body> */}
            </Table>
            <ProductList
                paymentRequestId={paymentRequestId}
                active={active}
                handleChange={handleChange}
            />
        </>
    )
}

export async function getStaticProps() {
    let result
    try {
        result = await API.graphql(graphqlOperation(listPaymentRequest))
    } catch (error) {
        console.log(error)
    }

    if (result.errors) {
        console.warn('Failed to fetch payment requests.', result.errors)
        return { props: { paymentRequests: [] } }
    }
    if (result.data !== null) {
        return {
            props: { paymentRequests: result.data }
        }
    }
}

export default AcceptedPaymentRequest
