import React, { useState } from 'react'
import createApp from '@shopify/app-bridge'
import { Redirect } from '@shopify/app-bridge/actions'
import gql from 'graphql-tag'
import { useQuery } from '@apollo/react-hooks'
import withApollo from '../lib/withApollo'
import { Card, DataTable, Button, Page } from '@shopify/polaris'

const listTransactions = gql`
    query listTransactions {
        listTransactions {
            items {
                id
                totalPrice
                currency
                products {
                    id
                    title
                    bonusPercentage
                    priceAmount
                    priceCurrency
                    image
                }
                customer {
                    id
                    firstName
                    lastName
                    phone
                    email
                }
                createdAt
            }
        }
    }
`

const Index = () => {
    console.log('Hi there Zaven jan')
    const { loading, data, error } = useQuery(listTransactions)

    const app = createApp({
        apiKey: '3b01063bac3031d13101100ef3e44fd5',
        shopOrigin: 'transactions-avanta.myshopify.com'
    })

    const redirect = Redirect.create(app)
    if (loading) {
        return <div>Loading...</div>
    }

    if (error) {
        console.log('Some error occured Zaven', error)
    }

    const rows = data.listTransactions.items.map((transaction) => [
        <Button>{transaction.id}</Button>,
        transaction.totalPrice,
        transaction.totalBonusAmount,
        transaction.createdAt,
        transaction.customer[0].email
    ])

    console.log('Rows', rows)
    console.log('Here is transaction data Zvo', data)
    return (
        <Page title="Transactions">
            <Button
                primary
                onClick={() =>
                    redirect.dispatch(Redirect.Action.REMOTE, {
                        url: 'https://dev.d3ivgpkzuz6hkr.amplifyapp.com',
                        newContext: true
                    })
                }>
                Create transaction
            </Button>
            <Card>
                <DataTable
                    columnContentTypes={['text', 'text', 'text', 'text']}
                    headings={[
                        'OrderId',
                        'TotalPrice',
                        'TotalBonusAmount',
                        'CreatedAt',
                        'Customer'
                    ]}
                    rows={rows}
                />
            </Card>
        </Page>
    )
}

export default withApollo(Index)
