import React, { useState, useCallback, useEffect } from 'react'
import createApp from '@shopify/app-bridge'
import { Redirect } from '@shopify/app-bridge/actions'
import { TitleBar } from '@shopify/app-bridge-react'
import gql from 'graphql-tag'
import { useQuery, useLazyQuery } from '@apollo/react-hooks'
import {
    Card,
    DataTable,
    Button,
    Page,
    TextField,
    ChoiceList,
    Filters,
    Icon
} from '@shopify/polaris'
import { SearchMajorMonotone } from '@shopify/polaris-icons'
import withApollo from '../lib/withApollo'
import { toCurrency } from '../utils/helper'

const listTransactions = gql`
    query listTransactions($limit: Int, $nextToken: String) {
        listTransactions(limit: $limit, nextToken: $nextToken) {
            nextToken
            items {
                id
                totalPrice
                totalBonusAmount
                currency
                products {
                    id
                    title
                    bonusPercentage
                    priceAmount
                    priceCurrency
                    image
                }
                customer {
                    id
                    firstName
                    lastName
                    phone
                    email
                }
                createdAt
            }
        }
    }
`

const Index = () => {
    // const { loading, data, error } = useQuery(listTransactions)
    const [getTransactions, { loading: lazyLoading, data: lazyData }] = useLazyQuery(
        listTransactions
    )
    const [searchValue, setSearchValue] = useState(null)
    const [nextPaginateToken, setNextPaginateToken] = useState(null)
    const [previousPaginateTokens, setPreviousPaginateTokens] = useState([])
    const handleSearchInput = useCallback((newValue) => setSearchValue(newValue), [])

    const fetchTransactions = useCallback(async () => {
        try {
            await getTransactions({ variables: { limit: 7 } })
            setNextPaginateToken(lazyData.listTransactions.nextToken)
        } catch (error) {
            console.log(error)
        }
    }, [lazyData, setNextToken])

    useEffect(() => {
        fetchTransactions()
    }, [fetchTransactions])

    console.log('Searching value', searchValue)

    console.log('Next token', nextToken)

    const app = createApp({
        apiKey: '3b01063bac3031d13101100ef3e44fd5',
        shopOrigin: 'transactions-avanta.myshopify.com'
    })

    const redirect = Redirect.create(app)
    // if (loading) {
    //     return <div>Loading...</div>
    // }

    if (lazyLoading) {
        return <div>Lazy loading...</div>
    }

    // if (error) {
    //     console.log('Some error occured Zaven', error)
    // }

    // const formatDate = (rowDate) => {
    //     const date = new Date(rowDate)
    //     const year = new Intl.DateTimeFormat('en', { year: 'numeric' }).format(date)
    //     const month = new Intl.DateTimeFormat('en', { month: 'short' }).format(date)
    //     const day = new Intl.DateTimeFormat('en', { day: '2-digit' }).format(date)

    //     return `${day}-${month}-${year}`
    // }

    // console.log('Data Zaven jan', data)

    // console.log('Page count', Math.ceil(data.listTransactions.items.length / 7))

    // const rows = data.listTransactions.items
    //     .filter((transaction) => {
    //         if (searchValue == null) {
    //             return transaction
    //         } else if (
    //             transaction.customer[0].phone.toLowerCase().includes(searchValue.toLowerCase())
    //         ) {
    //             return transaction
    //         }
    //     })
    //     .map((transaction) => [
    //         <Button
    //             primary
    //             onClick={() =>
    //                 redirect.dispatch(Redirect.Action.ADMIN_PATH, {
    //                     path: `/customers/${transaction.customer[0].id}`,
    //                     newContext: true
    //                 })
    //             }>
    //             {transaction.customer[0].phone}
    //         </Button>,
    //         toCurrency(Number(transaction.totalPrice)),
    //         toCurrency(Number(transaction.totalBonusAmount)),
    //         formatDate(transaction.createdAt)
    //     ])

    // console.log('Rows', rows)
    // console.log('Here is transaction data Zvo', data)

    console.log('Data from lazy query', lazyData)
    console.log('Next paginate token', nextPaginateToken)
    console.log('Previous paginate Tokens', previousPaginateTokens)
    return (
        <Page title="Transactions">
            <TextField
                label="Search Product"
                value={searchValue}
                onChange={handleSearchInput}
                prefix={<Icon source={SearchMajorMonotone} />}
                placeholder="Search by Customer phone number"
            />
            <Button
                onClick={() => {
                    getTransactions({ variables: { nextToken: nextPaginateToken, limit: 4 } })
                    setNextPaginateToken(previousPaginateTokens.push(nextPaginateToken))
                }}>
                Paginate next
            </Button>
            {/* <Button onClick={() => getTransactions()}>Paginate previous</Button> */}
            <Button
                primary
                onClick={() =>
                    redirect.dispatch(Redirect.Action.REMOTE, {
                        url: 'https://dev.d3ivgpkzuz6hkr.amplifyapp.com',
                        newContext: true
                    })
                }>
                Create transaction
            </Button>
            {/* <Card>
                <DataTable
                    columnContentTypes={['text', 'text', 'text', 'text']}
                    headings={[
                        'Customer phone number',
                        'TotalPrice',
                        'TotalBonusAmount',
                        'CreatedAt'
                    ]}
                    rows={rows}
                />
            </Card> */}
        </Page>
    )
}

export default withApollo(Index)
