import { API, graphqlOperation } from "aws-amplify"
import config from "../../aws-exports"
import { listPaymentRequest } from "../../graphql/queries"
import { updatePaymentRequest } from "../../graphql/mutation"

API.configure(config)

export const fetchPaymentRequest = ({ branchId, status, limit }) => {
  return API.graphql(graphqlOperation(listPaymentRequest, { branchId, status, limit }))
}

export const updatePayment = ({ paymentId, status }) => {
  return API.graphql(
    graphqlOperation(updatePaymentRequest, {
      input: {
        id: paymentId,
        status,
      },
    })
  )
}
