import { useState, useEffect, useCallback } from 'react'
import { Card, Button, TextField, Form, FormLayout } from '@shopify/polaris'
import { Auth, API, graphqlOperation } from 'aws-amplify'
import config from '../aws-exports'

API.configure(config)

const Profile = () => {
    const [formState, updateFormState] = useState({
        username: '',
        password: ''
    })
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [user, updateUser] = useState(null)

    useEffect(() => {
        checkUser()
    }, [])

    const checkUser = async () => {
        try {
            const user = await Auth.currentAuthenticatedUser()

            if (!user) {
                return
            }
            updateUser(user)
            console.log('User: ', user)
        } catch (error) {
            console.log(error)
        }
    }

    // const onChange = (e) => {
    //     updateFormState({ ...formState, [e.target.name]: e.target.value })
    //     console.log('Target properties', e.target)
    // }

    const onChangeUsername = useCallback((newValue) => {
        setUsername(newValue)
        console.log('Value username', newValue)
    }, [])

    const onChangePassword = useCallback((newValue) => {
        setPassword(newValue)
        console.log('Value password', newValue)
    }, [])

    const signIn = async (e) => {
        e.preventDefault()

        const { username, password } = formState

        const signedInUser = await Auth.signIn(username, password)

        console.log('SignedIn user: ', signedInUser)
        updateFormState(() => ({ ...formState, formType: 'signedIn' }))

        const currentAuthenticatedUser = await Auth.currentAuthenticatedUser()

        console.log('Current authenticated user', currentAuthenticatedUser)
    }

    // const { username, password } = formState

    // console.log('State data', formState)
    console.log('User state: ', user)

    return (
        <div className="authentication-container">
            {!user ? (
                <Card title="Log into your branch" sectioned>
                    <Form onSubmit={signIn}>
                        <FormLayout>
                            <TextField
                                // name="username"
                                value={username}
                                onChange={onChange}
                                label="Username"
                            />
                            <TextField
                                // name="password"
                                value={password}
                                onChange={onChange}
                                label="Password"
                            />
                            <Button primary submit>
                                Login
                            </Button>
                        </FormLayout>
                    </Form>
                </Card>
            ) : (
                <div>Hello from signed in section</div>
            )}
        </div>
    )
}

export default Profile
