import React, { useState, useEffect } from 'react'
import { useRouter } from 'next/router'
import createApp from '@shopify/app-bridge'
import { Redirect } from '@shopify/app-bridge/actions'
import { ClientApplication } from '@shopify/app-bridge'
import { API, graphqlOperation } from 'aws-amplify'
import gql from 'graphql-tag'
import { Page, Button, Icon, Card, DataTable } from '@shopify/polaris'
import { TransactionMajorMonotone } from '@shopify/polaris-icons'
import config from '../../aws-exports'
import BranchContext from '../../context/branchContext'
import { Divider } from 'semantic-ui-react'

API.configure(config)

const getBranchById = gql`
    query getBranch($id: ID!) {
        getBranch(id: $id) {
            transactions(sortDirection: DESC) {
                items {
                    branchId
                    createdAt
                    currency
                    note
                    customer {
                        email
                        id
                        firstName
                        lastName
                        phone
                    }
                    totalBonusAmount
                    totalPrice
                    updatedAt
                    sortDate
                }
            }
            branchName
            adminId
            id
            branchProducts {
                items {
                    branchId
                    createdAt
                    id
                    productId
                    tags
                    updatedAt
                }
            }
        }
    }
`

const Branch = () => {
    const [branchInfo, setBranchInfo] = useState('')

    const getBranch = async () => {
        try {
            const branch = await API.graphql(
                graphqlOperation(getBranchById, { id: localStorage.getItem('branchId') })
            )
            setBranchInfo(branch.data.getBranch)
        } catch (error) {
            console.log(error)
        }
    }

    useEffect(() => {
        console.log('Localstorage branch id', localStorage.getItem('branchId'))
        console.log('Localstorage branch id', typeof localStorage.getItem('branchId'))
        getBranch()
    }, [])

    console.log('Branch info', branchInfo)

    return (
        <>
            <Page title={branchInfo && branchInfo.branchName}></Page>
            {branchInfo && (
                <Card>
                    <DataTable
                        columnContentTypes={['text', 'text', 'text', 'text']}
                        headings={[
                            'Customer phone number',
                            'TotalPrice',
                            'TotalBonusAmount',
                            'Minus from Bonus Amount',
                            'CreatedAt'
                        ]}
                        rows={
                            branchInfo &&
                            branchInfo.transactions.items
                                .filter((transaction) => {
                                    if (searchValue == null) {
                                        return transaction
                                    } else if (
                                        transaction.customer[0].phone
                                            .toLowerCase()
                                            .includes(searchValue.toLowerCase())
                                    ) {
                                        return transaction
                                    }
                                })
                                .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
                                .map((transaction) => [
                                    <Button
                                        primary
                                        onClick={() =>
                                            redirect.dispatch(Redirect.Action.ADMIN_PATH, {
                                                path: `/customers/${transaction.customer[0].id}`,
                                                newContext: true
                                            })
                                        }>
                                        {transaction.customer[0].phone}
                                    </Button>,
                                    toCurrency(Number(transaction.totalPrice)),
                                    toCurrency(Number(transaction.totalBonusAmount)),
                                    transaction.note === null
                                        ? ''
                                        : toCurrency(extractNumbersFromString(transaction.note)),
                                    formatDate(transaction.createdAt)
                                ])
                        }
                    />
                    {/* <Pagination
                    hasPrevious={previousPaginateTokens.length > 0}
                    onPrevious={paginatePrevious}
                    hasNext={nextPaginateToken}
                    onNext={paginateNext}
                /> */}
                </Card>
            )}
        </>
    )
}

export default Branch
