                            value={username}
                            onChange={onChangeUsername}
                            label="Branch admin username"
                        />
                        <TextField
                            value={email}
                            onChange={onChangeEmail}
                            label="Branch admin email"
                        />
                        <TextField
                            type="password"
                            value={password}
                            onChange={onChangePassword}
                            label="Branch admin password"
                        />
                        <Button primary submit>
                            Login
                        </Button>
                    </FormLayout>
                </Form>
            </Modal>
        </>
    )
}

export default BranchConsole
