import React from "react"
import { useMutation } from "react-query"
import { AuthService } from "../services"

const useLogin = () => {
  const [login, { isLoading, error, data }] = useMutation(AuthService.login)

  return {
    login,
    isLoading,
    data,
    error,
  }
}

export default useLogin
