import React, { useState, useCallback } from 'react'
import axios from 'axios'
import { v4 as uuidv4 } from 'uuid'
import { API, graphqlOperation } from 'aws-amplify'
import gql from 'graphql-tag'
import { useQuery } from '@apollo/client'
import { Modal, Thumbnail, Checkbox, TextStyle } from '@shopify/polaris'
import { Table, Header } from 'semantic-ui-react'
import { toCurrency, formatDate } from '../utils/helper'
import config from '../aws-exports'
import BranchRow from './BranchRow'

API.configure(config)

const listProducts = gql`
    query Products {
        products(first: 100) {
            edges {
                node {
                    id
                    title
                    tags
                    description(truncateAt: 100)
                    variants(first: 1) {
                        edges {
                            node {
                                id
                                price
                            }
                        }
                    }
                    images(first: 1) {
                        edges {
                            node {
                                originalSrc
                            }
                        }
                    }
                }
            }
        }
    }
`

const createBranchProduct = gql`
    mutation createBranchProduct($input: CreateBranchProductInput!) {
        createBranchProduct(input: $input) {
            id
            branchId
            productId
            tags
        }
    }
`
const productUpdate = gql`
    mutation productUpdate($input: ProductInput!) {
        productUpdate(input: $input) {
            product {
                id
                metafields(first: 100) {
                    edges {
                        node {
                            namespace
                            key
                            value
                        }
                    }
                }
            }
        }
    }
`
const ProductsList = ({ active, handleChange, branchId, branchName }) => {
    const { loading, error, data } = useQuery(listProducts)
    const [rowId, setRowId] = useState([])
    const [state, setState] = useState({
        products: []
    })
    const [searchValue, setSearchValue] = useState(null)
    const handleSearchInput = useCallback((newValue) => setSearchValue(newValue), [])

    // const updateProducts = () => {
    //     Promise.all(
    //         state.products.map((product) => {
    //             const updatedProduct = axios({
    //                 url: 'https://transactions-avanta.myshopify.com/admin/api/2020-07/graphql.json',
    //                 method: 'post',
    //                 headers: {
    //                     'Content-Type': 'application/json',
    //                     'X-Shopify-Access-Token': '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'
    //                 },
    //                 data: {
    //                     query: productUpdate,
    //                     variables: {
    //                         id: product.id,
    //                         metafields: {
    //                             namespace: branchName,
    //                             key: branchId,
    //                             value: product.tags[0],
    //                             valueType: 'STRING'
    //                         }
    //                     }
    //                 }
    //             })
    //         })
    //     )
    //         .then((res) => console.log('Updated product', res))
    //         .catch((err) => console.log(err))
    // }

    const updateProducts = async () => {
        try {
            await axios({
                url: 'https://wyjl8np1l8.execute-api.us-east-1.amazonaws.com/test/update-product',
                method: 'POST',
                data: {
                    path: '/update-product',
                    products: state.products
                }
            })
            console.log('Check your lambda console')
        } catch (error) {
            console.log(error)
        }
    }

    if (loading) {
        return <div>Loading products...</div>
    }

    if (error) {
        console.log('Some error Zaven', error)
        return <div>Some error occured</div>
    }

    const addProductsToBranch = () => {
        Promise.all(
            state.products.map((product) => {
                API.graphql(
                    graphqlOperation(createBranchProduct, {
                        input: {
                            id: uuidv4(),
                            branchId: branchId,
                            productId: product.id,
                            tags: product.tags
                        }
                    })
                )
            })
        )
            .then((res) => console.log('Branch products', res))
            .catch((err) => console.log(err))
    }

    // const updateProducts = async () => {
    //     try {
    //         await axios({
    //             url: 'http://localhost:3000/updateProduct',
    //             method: 'POST',
    //             data: {
    //                 products: state.products
    //             }
    //         })
    //     } catch (error) {
    //         console.log(error)
    //     }
    // }

    console.log('State data from product list', state)

    return (
        <Modal
            open={active}
            onClose={handleChange}
            title="Choose products"
            primaryAction={{
                content: 'Add products to branch',
                onAction: () => {
                    addProductsToBranch()
                    updateProducts()
                    handleChange()
                }
            }}>
            <Modal.Section>
                <Table celled striped selectable>
                    <Table.Header>
                        <Table.Row>
                            <Table.HeaderCell>Products</Table.HeaderCell>
                            <Table.HeaderCell textAlign="center">Price</Table.HeaderCell>
                            <Table.HeaderCell>Bonus Percentage</Table.HeaderCell>
                        </Table.Row>
                    </Table.Header>
                    <Table.Body>
                        {data &&
                            data.products.edges
                                .filter((product) => {
                                    if (searchValue == null) {
                                        return product
                                    } else if (
                                        product.node.title
                                            .toLowerCase()
                                            .includes(searchValue.toLowerCase())
                                    ) {
                                        return product
                                    }
                                })
                                .map((product) => (
                                    <BranchRow
                                        product={product}
                                        state={state}
                                        setState={setState}
                                    />
                                    // <Table.Row
                                    //     key={product.node.id}
                                    //     >
                                    //     <Table.Cell>
                                    //         <Header as="h4" image className="product-header">
                                    //             <Checkbox
                                    //                 checked={
                                    //                     rowId.includes(product.node.id) && true
                                    //                 }
                                    //                 onClick={() => {
                                    //                     if (rowId.includes(product.node.id)) {
                                    //                         setRowId(
                                    //                             rowId.filter((id) => id !== product.node.id)
                                    //                         )
                                    //                         setState({
                                    //                             products: state.products.filter(
                                    //                                 (filteredProduct) =>
                                    //                                     filteredProduct.node.id !==
                                    //                                     product.node.id
                                    //                             )
                                    //                         })
                                    //                     } else {
                                    //                         setRowId([...rowId, product.node.id])
                                    //                         setState({
                                    //                             ...state,
                                    //                             products: [...state.products, product]
                                    //                         })
                                    //                     }
                                    //                 }}
                                    //             />
                                    //             <Thumbnail
                                    //                 source={
                                    //                     product.node.images.edges[0] &&
                                    //                     product.node.images.edges[0].node
                                    //                         .originalSrc
                                    //                 }
                                    //                 size="small"
                                    //                 alt=""
                                    //             />
                                    //             <Header.Content>
                                    //                 {product.node.title}
                                    //             </Header.Content>
                                    //         </Header>
                                    //     </Table.Cell>
                                    //     <Table.Cell>
                                    //         <TextStyle variation="subdued">
                                    //             {product.node.description}
                                    //         </TextStyle>
                                    //     </Table.Cell>
                                    //     <Table.Cell textAlign="center">
                                    //         <TextStyle variation="subdued">
                                    //             {product.node.variants.edges[0] &&
                                    //                 toCurrency(
                                    //                     product.node.variants.edges[0].node.price
                                    //                 )}
                                    //         </TextStyle>
                                    //     </Table.Cell>
                                    // </Table.Row>
                                ))}
                    </Table.Body>
                    <Table.Footer fullWidth>
                        <Table.Row></Table.Row>
                    </Table.Footer>
                </Table>
            </Modal.Section>
        </Modal>
    )
}

export default ProductsList
