import { API, graphqlOperation } from "aws-amplify";
import { v4 as uuidv4 } from "uuid";
import { getBranchById } from "../../graphql/queries";
import { createBranch } from "../../graphql/mutation";
import config from "../../aws-exports";
API.configure(config);
export const getBranch = (key, {
  branchId
}) => API.graphql(graphqlOperation(getBranchById, {
  id: branchId
}));
export const createBranchEntity = ({
  userSub,
  username,
  branchName
}) => API.graphql(graphqlOperation(createBranch, {
  input: {
    adminId: userSub,
    branchUsername: username,
    branchName: branchName,
    id: uuidv4()
  }
}));
