import React, { useState, useEffect } from 'react'
import { useRouter } from 'next/router'
import { ClientApplication } from '@shopify/app-bridge'
import { API, graphqlOperation } from 'aws-amplify'
import gql from 'graphql-tag'
import config from '../../aws-exports'
import BranchContext from '../../context/branchContext'
import { BarcodeMajorMonotone } from '@shopify/polaris-icons'

API.configure(config)

const getBranchById = gql`
    query getBranch($id: ID) {
        getBranch(id: $id) {
            branchName
            adminId
            id
            branchProducts {
                items {
                    branchId
                    createdAt
                    id
                    productId
                    tags
                    updatedAt
                }
            }
        }
    }
`

const Branch = () => {
    const router = useRouter()
    const [branchId, setBranchId] = useState('')
    console.log('Router from branch', router.asPath.split('/')[2].split('?')[0])
    console.log('Type of branch Id', typeof router.asPath.split('/')[2].split('?')[0])

    const fetchBranch = async () => {
        console.log('Branch id', branchId)
        try {
            const branch = await API.graphql(graphqlOperation(getBranchById, { id: branchId }))
            console.log('Fetched branch', branch)
        } catch (error) {
            console.log(error)
        }
    }
    useEffect(() => {
        setBranchId(router.asPath.split('/')[2].split('?')[0])
        if (branchId === '[branchId]') {
            console('No branch id yet')
            return
        }
        fetchBranch()
    }, [branchId, setBranchId])

    return <div>Branch</div>
}

export default Branch
