import React, { useState, useEffect, useRef, useCallback } from 'react'
import { Page, Button, Icon, Modal, Form, FormLayout, TextField, Card } from '@shopify/polaris'
import { Table, Popup, Header } from 'semantic-ui-react'
import ProductList from './ProductsList'

const BranchConsole = () => {
    const [active, setActive] = useState(false)
    const [username, setUsername] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    const onChangeUsername = useCallback((username) => {
        setUsername(username)
    }, [])

    const onChangePassword = useCallback((password) => {
        setPassword(password)
    }, [])

    const onChangeEmail = useCallback((email) => {
        setEmail(email)
    }, [])

    const handleChange = () => {
        setActive(!active)
    }
    return (
        <>
            <Button onClick={handleChange} primary>
                Create branch
            </Button>
            <Table selectable celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Branch Name</Table.HeaderCell>
                        <Table.HeaderCell>Created At</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>
                {/* <Table.Body>
                    {paymentRequestItems &&
                        paymentRequestItems
                            .sort((a, b) => new Date(b.updatedAt) - new Date(a.updatedAt))
