import React, { useState, useEffect, useCallback } from 'react'
import { API, graphqlOperation } from 'aws-amplify'
import gql from 'graphql-tag'
import { Table, Icon, Popup, Header } from 'semantic-ui-react'
import { Badge, Pagination } from '@shopify/polaris'
import { toCurrency, formatDate } from '../utils/helper'
import config from '../aws-exports'

API.configure(config)

const listPaymentRequest = gql`
    query listPaymentRequest {
        listPaymentRequests(filter: { status: { eq: APPROVED } }) {
            items {
                bonusAmount
                createdAt
                customerId
                id
                orderId
                status
                updatedAt
            }
            nextToken
        }
    }
`

const AcceptedPaymentRequest = () => {
    const [nextPaginateToken, setNextPaginateToken] = useState('')
    const [previousPaginateTokens, setPreviousPaginateTokens] = useState([])
    const [paymentRequestItems, setPaymentRequestItems] = useState([])
    const [pageOffset, setPageOffset] = useState(0)
    const [itemsPerPage, setItemsPerPage] = useState(7)

    const fetchAcceptedPayments = useCallback(async () => {
        try {
            const res = await API.graphql(graphqlOperation(listPaymentRequest, { limit: 3 }))
            setNextPaginateToken(res.data.listPaymentRequests.nextToken)
            setPaymentRequestItems(res.data.listPaymentRequests.items)
            console.log('Accepted payments', res.data)
        } catch (error) {
            console.log(error)
        }
    }, [])

    useEffect(() => {
        fetchAcceptedPayments()
    }, [fetchAcceptedPayments])

    // const paginateNext = () => {
    //     setPageOffset(pageOffset + itemsPerPage)
    // }

    const paginateNext = async () => {
        setPreviousPaginateTokens([...previousPaginateTokens, nextPaginateToken])
        try {
            const res = await API.graphql(
                graphqlOperation(listPaymentRequest, { limit: 6, nextToken: nextPaginateToken })
            )

            console.log('Data from pagination', res)
            setNextPaginateToken(res.data.listPaymentRequests.nextToken)
            setPaymentRequestItems(res.data.listPaymentRequests.items)
        } catch (error) {
            console.log(error)
        }
    }

    const paginatePrevious = async () => {
        const previousToken =
            previousPaginateTokens[
                previousPaginateTokens.length === 1 ? 0 : previousPaginateTokens.length - 2
            ]
        setPreviousPaginateTokens([
            ...previousPaginateTokens.slice(0, previousPaginateTokens.length - 1)
        ])
        try {
            if (previousPaginateTokens.length === 1) {
                const res = await API.graphql(graphqlOperation(listPaymentRequest))
                setNextPaginateToken(res.data.listPaymentRequests.nextToken)
                setPaymentRequestItems(res.data.listPaymentRequests.items)
            } else {
                const data = await API.graphql(
                    graphqlOperation(listTransactions, { nextToken: previousToken })
                )
                setNextPaginateToken(res.data.listPaymentRequests.nextToken)
                setPaymentRequestItems(res.data.listPaymentRequests.items)
            }
        } catch (error) {
            console.log(error)
        }
    }

    console.log('Next paginate token', nextPaginateToken)
    console.log('Accepted state data', paymentRequestItems)

    return (
        <Table celled>
            <Table.Header>
                <Table.Row>
                    <Table.HeaderCell>Customer Id</Table.HeaderCell>
                    <Table.HeaderCell>Order Id</Table.HeaderCell>
                    <Table.HeaderCell>Bonus Amount</Table.HeaderCell>
                    <Table.HeaderCell>Created At</Table.HeaderCell>
                    <Table.HeaderCell>Updated At</Table.HeaderCell>
                    <Table.HeaderCell>Status</Table.HeaderCell>
                </Table.Row>
            </Table.Header>
            <Table.Body>
                {paymentRequestItems &&
                    paymentRequestItems.splice(pageOffset, itemsPerPage).map((item) => (
                        <Table.Row key={item.id}>
                            <Table.Cell>
                                <Badge size="small">{item.customerId}</Badge>
                            </Table.Cell>
                            <Table.Cell>
                                <Badge size="small">{item.orderId}</Badge>
                            </Table.Cell>
                            <Table.Cell>{toCurrency(item.bonusAmount)}</Table.Cell>
                            <Table.Cell>{formatDate(item.createdAt)}</Table.Cell>
                            <Table.Cell>{formatDate(item.updatedAt)}</Table.Cell>
                            <Table.Cell>
                                <Badge size="small" progress="complete" status="success">
                                    {item.status}
                                </Badge>
                            </Table.Cell>
                        </Table.Row>
                    ))}
            </Table.Body>
            <Table.Footer>
                <Pagination
                    hasPrevious={previousPaginateTokens.length > 0}
                    onPrevious={paginatePrevious}
                    hasNext={nextPaginateToken}
                    onNext={paginateNext}
                />
            </Table.Footer>
        </Table>
    )
}

export default AcceptedPaymentRequest
