import React from "react"
import { useQuery } from "react-query"
import { PaymentService } from "../services"

const usePaginatePaymentRequests = (branchId, status, limit, nextToken) => {
  const paymentRequests = useQuery(
    ["payments-pagination", { branchId, status, limit, nextToken }],
    PaymentService.fetchPaymentRequest,
    { enabled: false }
  )
  return paymentRequests
}

export default usePaginatePaymentRequests
