import React, { useState, useEffect } from 'react'
import { useRouter } from 'next/router'
import { API, graphqlOperation } from 'aws-amplify'
import gql from 'graphql-tag'
import config from '../../aws-exports'

API.configure(config)

const getBranchById = gql`
    query getBranch {
        getBranch(id: "54b3c39c-ae15-4875-8677-d949c59fb220") {
            branchName
            adminId
            id
            branchProducts {
                items {
                    branchId
                    createdAt
                    id
                    productId
                    tags
                    updatedAt
                }
            }
        }
    }
`

const Branch = () => {
    const [branchId, setBranchId] = useState(router.asPath.split('/')[2].split('?')[0])
    const router = useRouter()
    console.log('Router from branch', router.asPath.split('/')[2].split('?')[0])
    console.log('Type of branch Id', typeof router.asPath.split('/')[2].split('?')[0])

    const fetchBranch = async () => {
        if (!branchId) {
            return
        }
        console.log('Branch id', branchId)
        try {
            const branch = await API.graphql(graphqlOperation(getBranchById))
            console.log('Fetched branch', branch)
        } catch (error) {
            console.log(error)
        }
    }
    useEffect(() => {
        fetchBranch()
    }, [branchId])
    return <div>Branch</div>
}

export default Branch
