import React, { useState, useEffect } from 'react'
import { useRouter } from 'next/router'
import { ClientApplication } from '@shopify/app-bridge'
import { API, graphqlOperation } from 'aws-amplify'
import gql from 'graphql-tag'
import config from '../../aws-exports'
import BranchContext from '../../context/branchContext'
import { BarcodeMajorMonotone } from '@shopify/polaris-icons'

API.configure(config)

const getBranchById = gql`
    query getBranch($id: ID) {
        getBranch(id: $id) {
            branchName
            adminId
            id
            branchProducts {
                items {
                    branchId
                    createdAt
                    id
                    productId
                    tags
                    updatedAt
                }
            }
        }
    }
`

const Branch = () => {
    const getBranch = async () => {
        try {
            const branch = await API.graphql(
                graphqlOperation(getBranchById, { id: localStorage.getItem('branchId').toString() })
            )
            console.log('Branch from branch page', branch)
        } catch (error) {
            console.log(error)
        }
    }

    useEffect(() => {
        console.log('Localstorage branch id', localStorage.getItem('branchId'))
        console.log('Localstorage branch id', typeof localStorage.getItem('branchId'))
        getBranch()
    }, [])

    return <div>Branch</div>
}

export default Branch
