import React, { useState, useCallback, useEffect } from 'react'
import axios from 'axios'
import { API, graphqlOperation } from 'aws-amplify'
import createApp from '@shopify/app-bridge'
import { Redirect } from '@shopify/app-bridge/actions'
import gql from 'graphql-tag'
import { print } from 'graphql'
import {
    Card,
    DataTable,
    Button,
    Page,
    Pagination,
    TextField,
    ChoiceList,
    Filters,
    Icon,
    Stack
} from '@shopify/polaris'
import {
    SearchMajorMonotone,
    TransactionMajorMonotone,
    BalanceMajorMonotone
} from '@shopify/polaris-icons'
import { toCurrency, formatDate } from '../utils/helper'
import config from '../aws-exports'

API.configure(config)

const listTransactions = gql`
    query listTransactions($limit: Int, $nextToken: String) {
        listTransactions(limit: $limit, nextToken: $nextToken) {
            nextToken
            items {
                id
                totalPrice
                totalBonusAmount
                currency
                products {
                    id
                    title
                    bonusPercentage
                    priceAmount
                    priceCurrency
                    image
                }
                customer {
                    id
                    firstName
                    lastName
                    phone
                    email
                }
                createdAt
            }
        }
    }
`

const Index = ({ transactions }) => {
    const [searchValue, setSearchValue] = useState(null)
    const [transactionItems, setTransactionItems] = useState(transactions.listTransactions.items)
    const [nextPaginateToken, setNextPaginateToken] = useState(
        transactions.listTransactions.nextToken
    )
    const [previousPaginateTokens, setPreviousPaginateTokens] = useState([])
    const [fetchCount, setFetchCount] = useState(0)
    const handleSearchInput = useCallback((newValue) => setSearchValue(newValue), [])

    console.log('Transactions from amplify', transactions)

    // const fetchTransactions = useCallback(async () => {
    //     try {
    //         const data = await axios({
    //             url:
    //                 'https://kytejmxrrrgahiikcodklhf6rq.appsync-api.us-east-1.amazonaws.com/graphql',
    //             method: 'post',
    //             headers: {
    //                 'x-api-key': 'da2-iwznaxe3h5bxznagzuctrhqekm'
    //             },
    //             data: {
    //                 query: print(listTransactions),
    //                 variables: {
    //                     limit: 5
    //                 }
    //             }
    //         })
    //         setTransactionItems(data.data.data.listTransactions.items)
    //         setNextPaginateToken(data.data.data.listTransactions.nextToken)
    //     } catch (error) {
    //         console.log(error)
    //     }
    // }, [])

    // useEffect(() => {
    //     fetchTransactions()
    // }, [fetchTransactions])

    const app = createApp({
        apiKey: '3b01063bac3031d13101100ef3e44fd5',
        shopOrigin: 'transactions-avanta.myshopify.com'
    })

    const redirect = Redirect.create(app)

    const paginateNext = async () => {
        setPreviousPaginateTokens([...previousPaginateTokens, nextPaginateToken])
        try {
            const data = await axios({
                url:
                    'https://kytejmxrrrgahiikcodklhf6rq.appsync-api.us-east-1.amazonaws.com/graphql',
                method: 'post',
                headers: {
                    'x-api-key': 'da2-iwznaxe3h5bxznagzuctrhqekm'
                },
                data: {
                    query: print(listTransactions),
                    variables: {
                        limit: 5,
                        nextToken: nextPaginateToken
                    }
                }
            })
            setTransactionItems(data.data.data.listTransactions.items)
            setNextPaginateToken(data.data.data.listTransactions.nextToken)
        } catch (error) {
            console.log(error)
        }
    }

    const paginatePrevious = async () => {
        const previousToken =
            previousPaginateTokens[
                previousPaginateTokens.length === 1 ? 0 : previousPaginateTokens.length - 2
            ]

        console.log('previous token Zvoshka', previousToken)

        setPreviousPaginateTokens([
            ...previousPaginateTokens.slice(0, previousPaginateTokens.length - 1)
        ])
        try {
            if (previousPaginateTokens.length === 1) {
                const data = await axios({
                    url:
                        'https://kytejmxrrrgahiikcodklhf6rq.appsync-api.us-east-1.amazonaws.com/graphql',
                    method: 'post',
                    headers: {
                        'x-api-key': 'da2-iwznaxe3h5bxznagzuctrhqekm'
                    },
                    data: {
                        query: print(listTransactions),
                        variables: {
                            limit: 5
                        }
                    }
                })
                setTransactionItems(data.data.data.listTransactions.items)
                setNextPaginateToken(data.data.data.listTransactions.nextToken)
            } else {
                const data = await axios({
                    url:
                        'https://kytejmxrrrgahiikcodklhf6rq.appsync-api.us-east-1.amazonaws.com/graphql',
                    method: 'post',
                    headers: {
                        'x-api-key': 'da2-iwznaxe3h5bxznagzuctrhqekm'
                    },
                    data: {
                        query: print(listTransactions),
                        variables: {
                            limit: 5,
                            nextToken: previousToken
                        }
                    }
                })
                setTransactionItems(data.data.data.listTransactions.items)
                setNextPaginateToken(data.data.data.listTransactions.nextToken)
            }
        } catch (error) {
            console.log(error)
        }
    }

    return (
        <Page title="Transactions">
            <TextField
                label="Search Product"
                value={searchValue}
                onChange={handleSearchInput}
                prefix={<Icon source={SearchMajorMonotone} />}
                placeholder="Search by Customer phone number"
            />
            <Stack>
                <Stack.Item fill>
                    <Button
                        id="Accept-Payment"
                        onClick={() =>
                            redirect.dispatch(
                                Redirect.Action.ADMIN_PATH,
                                '/apps/3b01063bac3031d13101100ef3e44fd5/accept-payment'
                            )
                        }>
                        <Icon source={BalanceMajorMonotone} /> Accept Payment
                    </Button>
                </Stack.Item>
                <Stack.Item>
                    <Button
                        id="Create-Transaction"
                        primary
                        onClick={() =>
                            redirect.dispatch(Redirect.Action.REMOTE, {
                                url: 'https://dev.d3ivgpkzuz6hkr.amplifyapp.com',
                                newContext: true
                            })
                        }>
                        <Icon source={TransactionMajorMonotone} /> Create transaction
                    </Button>
                </Stack.Item>
            </Stack>
            {transactionItems && (
                <Card>
                    <DataTable
                        columnContentTypes={['text', 'text', 'text', 'text']}
                        headings={[
                            'Customer phone number',
                            'TotalPrice',
                            'TotalBonusAmount',
                            'CreatedAt'
                        ]}
                        rows={
                            transactionItems &&
                            transactionItems
                                .filter((transaction) => {
                                    if (searchValue == null) {
                                        return transaction
                                    } else if (
                                        transaction.customer[0].phone
                                            .toLowerCase()
                                            .includes(searchValue.toLowerCase())
                                    ) {
                                        return transaction
                                    }
                                })
                                .map((transaction) => [
                                    <Button
                                        primary
                                        onClick={() =>
                                            redirect.dispatch(Redirect.Action.ADMIN_PATH, {
                                                path: `/customers/${transaction.customer[0].id}`,
                                                newContext: true
                                            })
                                        }>
                                        {transaction.customer[0].phone}
                                    </Button>,
                                    toCurrency(Number(transaction.totalPrice)),
                                    toCurrency(Number(transaction.totalBonusAmount)),
                                    formatDate(transaction.createdAt)
                                ])
                        }
                    />
                    <Pagination
                        hasPrevious={previousPaginateTokens.length > 0}
                        onPrevious={paginatePrevious}
                        hasNext={nextPaginateToken}
                        onNext={paginateNext}
                    />
                </Card>
            )}
        </Page>
    )
}

export async function getStaticProps() {
    let result
    try {
        result = await API.graphql(graphqlOperation(listTransactions, { limit: 5 }))
    } catch (error) {
        console.log(error)
    }

    if (result.errors) {
        console.warn('Failed to fetch todolist. ', result.errors)
        return { props: { transactions: [] } }
    }
    if (result.data !== null) {
        return { props: { transactions: result.data } }
    }
}

export default Index
