import React, { useState, useCallback, useEffect } from 'react'
import axios from 'axios'
import gql from 'graphql-tag'
import { print } from 'graphql'
import { API, graphqlOperation } from 'aws-amplify'
import { ResourcePicker } from '@shopify/app-bridge/actions'
import {
    Page,
    Badge,
    Avatar,
    TextStyle,
    Card,
    TextContainer,
    Modal,
    Checkbox,
    Thumbnail,
    Toast,
    Frame,
    Button,
    Tabs
} from '@shopify/polaris'
import { CustomersMajorMonotone } from '@shopify/polaris-icons'
import { Table, Icon, Popup, Header } from 'semantic-ui-react'
import { toCurrency, formatDate } from '../utils/helper'
import config from '../aws-exports'
import ProductList from '../components/ProductsList'
import AcceptedPaymentRequest from '../components/AcceptedPaymentRequest'
import PendingPaymentRequest from '../components/PendingPaymentRequest'
import DeclinedPaymentRequest from '../components/DeclinedPaymentRequest'

API.configure(config)

const listPaymentRequest = gql`
    query listPaymentRequest {
        listPaymentRequests {
            items {
                bonusAmount
                createdAt
                customerId
                id
                orderId
                status
                updatedAt
            }
        }
    }
`

const listProducts = gql`
    query Products {
        products(first: 100) {
            edges {
                node {
                    id
                    title
                    tags
                    description(truncateAt: 100)
                    variants(first: 1) {
                        edges {
                            node {
                                id
                                price
                            }
                        }
                    }
                    images(first: 1) {
                        edges {
                            node {
                                originalSrc
                            }
                        }
                    }
                }
            }
        }
    }
`

const createDraftOrder = gql`
    mutation CreateOrder($input: DraftOrderInput!) {
        draftOrderCreate(input: $input) {
            draftOrder {
                id
                customer {
                    id
                }
                email
                lineItems(first: 10) {
                    edges {
                        node {
                            title
                            quantity
                            originalUnitPrice
                            variant {
                                id
                                price
                                title
                                product {
                                    tags
                                    title
                                    images(first: 5) {
                                        edges {
                                            node {
                                                originalSrc
                                            }
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }
`

const completeOrder = gql`
    mutation CompleteOrder($id: ID!) {
        draftOrderComplete(id: $id, paymentPending: true) {
            draftOrder {
                customer {
                    id
                }
                email
                name
            }
        }
    }
`

const AcceptPayment = ({ paymentRequests }) => {
    const [paymentRequestItems, setPaymentRequestItems] = useState(
        paymentRequests.listPaymentRequests.items
    )
    const [openPicker, setOpenPicker] = useState(false)
    const [active, setActive] = useState(false)
    const [rowId, setRowId] = useState([])
    const [paymentRequestId, setPaymentRequestId] = useState('')
    const [searchValue, setSearchValue] = useState(null)
    const [activeToast, setActiveToast] = useState(false)
    const [selected, setSelected] = useState(0)

    const handleTabChange = useCallback((selectedTabIndex) => setSelected(selectedTabIndex), [])

    const handleSearchInput = useCallback((newValue) => setSearchValue(newValue), [])

    const handleChange = useCallback(() => setActive(!active), [active])

    const tabs = [
        {
            id: 'pending-payments',
            content: 'Pending Payment Requests',
            accessibilityLabel: 'Pending Requests',
            panelID: 'pending-payments-content'
        },
        {
            id: 'accepted-payments',
            content: 'Accepted Payment Requests',
            accessibilityLabel: 'Accepted Request',
            panelID: 'accepted-payments-content'
        },
        {
            id: 'declined-payments',
            content: 'Declined Payment Request',
            accessibilityLabel: 'Declined Requests',
            panelID: 'declined-payments-content'
        }
    ]

    const toastMarkup = activeToast ? (
        <Toast
            content="Order has been successfully created"
            onDismiss={() => setActiveToast(false)}
        />
    ) : null

    console.log('Payment id', paymentRequestId)

    const renderPaymentRequest = (selected) => {
        switch (selected) {
            case 0:
                return <PendingPaymentRequest paymentRequestItems={paymentRequestItems} />
            case 1:
                return <AcceptedPaymentRequest paymentRequestItems={paymentRequestItems} />
            case 2:
                return <DeclinedPaymentRequest paymentRequestItems={paymentRequestItems} />
            default:
                return
        }
    }

    return (
        <Frame>
            <Page title="Payment Request">
                <Card>
                    <Tabs tabs={tabs} selected={selected} onSelect={handleTabChange}>
                        <Card.Section title={tabs[selected].content}>
                            {renderPaymentRequest(selected)}
                        </Card.Section>
                    </Tabs>
                </Card>
                {/* <AcceptedPaymentRequest paymentRequestItems={paymentRequestItems} />
                <PendingPaymentRequest paymentRequestItems={paymentRequestItems} />
                <DeclinedPaymentRequest paymentRequestItems={paymentRequestItems} /> */}
                {/* <Table selectable celled>
                    <Table.Header>
                        <Table.Row>
                            <Table.HeaderCell>Customer Id</Table.HeaderCell>
                            <Table.HeaderCell>Order Id</Table.HeaderCell>
                            <Table.HeaderCell>Bonus Amount</Table.HeaderCell>
                            <Table.HeaderCell>Created At</Table.HeaderCell>
                            <Table.HeaderCell>Updated At</Table.HeaderCell>
                            <Table.HeaderCell>Status</Table.HeaderCell>
                            <Table.HeaderCell>Action</Table.HeaderCell>
                        </Table.Row>
                    </Table.Header>
                    <Table.Body>
                        {paymentRequestItems &&
                            paymentRequestItems.map((item) => (
                                <Table.Row key={item.id}>
                                    <Table.Cell>
                                        <Badge size="small">{item.customerId}</Badge>
                                    </Table.Cell>
                                    <Table.Cell>
                                        <Badge size="small">{item.orderId}</Badge>
                                    </Table.Cell>
                                    <Table.Cell>{toCurrency(item.bonusAmount)}</Table.Cell>
                                    <Table.Cell>{formatDate(item.createdAt)}</Table.Cell>
                                    <Table.Cell>{formatDate(item.updatedAt)}</Table.Cell>
                                    <Table.Cell>
                                        <Badge
                                            size="small"
                                            progress={
                                                item.status === 'PENDING'
                                                    ? 'partiallyComplete'
                                                    : item.status === 'APPROVED'
                                                    ? 'complete'
                                                    : 'incomplete'
                                            }
                                            status={
                                                item.status === 'PENDING'
                                                    ? 'attention'
                                                    : item.status === 'APPROVED'
                                                    ? 'success'
                                                    : 'critical'
                                            }>
                                            {item.status}
                                        </Badge>
                                    </Table.Cell>
                                    <Table.Cell className="actions-cell">
                                        <Popup
                                            content="Approve payment"
                                            trigger={
                                                <Icon
                                                    className="accept"
                                                    name="check"
                                                    onClick={() => {
                                                        setPaymentRequestId(item.id)
                                                        handleChange()
                                                    }}
                                                />
                                            }
                                        />
                                        <Popup
                                            content="Decline payment"
                                            trigger={<Icon className="decline" name="remove" />}
                                        />
                                    </Table.Cell>
                                </Table.Row>
                            ))}
                    </Table.Body>
                </Table> */}
                <ProductList
                    paymentRequestId={paymentRequestId}
                    active={active}
                    handleChange={handleChange}
                />
                {toastMarkup}
            </Page>
        </Frame>
    )
}

export async function getStaticProps() {
    let result
    try {
        result = await API.graphql(graphqlOperation(listPaymentRequest))
    } catch (error) {
        console.log(error)
    }

    if (result.errors) {
        console.warn('Failed to fetch payment requests.', result.errors)
        return { props: { paymentRequests: [] } }
    }
    if (result.data !== null) {
        return {
            props: { paymentRequests: result.data }
        }
    }
}

export default AcceptPayment
