import React, { useState, useCallback, useEffect } from 'react'
import axios from 'axios'
import gql from 'graphql-tag'
import { print } from 'graphql'
import { API, graphqlOperation } from 'aws-amplify'
import { ResourcePicker } from '@shopify/app-bridge/actions'
import { Page, Badge, Avatar, TextStyle, Card, TextContainer, Modal } from '@shopify/polaris'
import { CustomersMajorMonotone } from '@shopify/polaris-icons'
import { Table, TableCell, Icon, Popup } from 'semantic-ui-react'
import { toCurrency, formatDate } from '../utils/helper'
import config from '../aws-exports'

API.configure(config)

const listPaymentRequest = gql`
    query listPaymentRequest {
        listPaymentRequests {
            items {
                bonusAmount
                createdAt
                customerId
                id
                orderId
                status
                updatedAt
            }
        }
    }
`

const listProducts = gql`
    query Products {
        products(first: 100) {
            edges {
                node {
                    id
                    title
                    tags
                    variants(first: 1) {
                        edges {
                            node {
                                price
                            }
                        }
                    }
                    images(first: 1) {
                        edges {
                            node {
                                originalSrc
                            }
                        }
                    }
                }
            }
        }
    }
`

const AcceptPayment = ({ paymentRequests, products }) => {
    const [paymentRequestItems, setPaymentRequestItems] = useState(
        paymentRequests.listPaymentRequests.items
    )
    const [productItems, setProductItems] = useState(products)
    const [openPicker, setOpenPicker] = useState(false)
    const [active, setActive] = useState(true)

    const resourceName = {
        singular: 'customer',
        plural: 'customers'
    }

    const handleChange = useCallback(() => setActive(!active), [active])

    console.log('Payment requests from getStaticProps', paymentRequests)
    console.log('products data', products)
    console.log('Payment request items', paymentRequestItems)
    return (
        <Page title="Payment Request">
            <Table selectable celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Customer Id</Table.HeaderCell>
                        <Table.HeaderCell>Order Id</Table.HeaderCell>
                        <Table.HeaderCell>Bonus Amount</Table.HeaderCell>
                        <Table.HeaderCell>Created At</Table.HeaderCell>
                        <Table.HeaderCell>Updated At</Table.HeaderCell>
                        <Table.HeaderCell>Status</Table.HeaderCell>
                        <Table.HeaderCell>Action</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>
                <Table.Body>
                    {paymentRequestItems &&
                        paymentRequestItems.map((item) => (
                            <Table.Row key={item.id}>
                                <Table.Cell>
                                    <Badge size="small">{item.customerId}</Badge>
                                </Table.Cell>
                                <Table.Cell>
                                    <Badge size="small">{item.orderId}</Badge>
                                </Table.Cell>
                                <Table.Cell>{toCurrency(item.bonusAmount)}</Table.Cell>
                                <Table.Cell>{formatDate(item.createdAt)}</Table.Cell>
                                <Table.Cell>{formatDate(item.updatedAt)}</Table.Cell>
                                <Table.Cell>
                                    <Badge
                                        size="small"
                                        status={
                                            item.status === 'PENDING'
                                                ? 'attention'
                                                : item.status === 'APPROVED'
                                                ? 'success'
                                                : 'critical'
                                        }>
                                        {item.status}
                                    </Badge>
                                </Table.Cell>
                                <Table.Cell className="actions-cell">
                                    <Popup
                                        content="Approve payment"
                                        trigger={
                                            <Icon
                                                className="accept"
                                                name="check"
                                                onClick={handleChange}
                                            />
                                        }
                                    />
                                    <Popup
                                        content="Decline payment"
                                        trigger={
                                            <Icon
                                                className="decline"
                                                // color={hoveredItemId === item.id ? 'red' : ''}
                                                // className={
                                                //     hoveredItemId === item.id ? 'decline-hovered' : ''
                                                // }
                                                name="remove"
                                                // onMouseEnter={() => setHoveredItemId(item.id)}
                                                // onMouseLeave={() => setHoveredItemId(null)}
                                            />
                                        }
                                    />
                                </Table.Cell>
                            </Table.Row>
                        ))}
                </Table.Body>
            </Table>
            <div style={{ height: '500px' }}>
                <Modal
                    open={active}
                    onClose={handleChange}
                    title="Choose products"
                    primaryAction={{
                        content: 'Proceed',
                        onAction: handleChange
                    }}>
                    <Modal.Section>
                        <TextContainer>
                            <p>
                                Use Instagram posts to share your products with millions of people.
                                Let shoppers buy from your store without leaving Instagram.
                            </p>
                        </TextContainer>
                    </Modal.Section>
                </Modal>
            </div>
            <ResourcePicker resourceType="Product" open />
        </Page>
    )
}

export async function getStaticProps() {
    let result
    let products
    try {
        result = await API.graphql(graphqlOperation(listPaymentRequest))
        products = await axios({
            url: 'https://transactions-avanta.myshopify.com/admin/api/2020-07/graphql.json',
            method: 'post',
            headers: {
                'Content-Type': 'application/json',
                'X-Shopify-Access-Token': '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'
            },
            data: {
                query: print(listProducts)
            }
        })
    } catch (error) {
        console.log(error)
    }

    if (result.errors || products.errors) {
        console.warn('Failed to fetch payment requests.', result.errors)
        return { props: { paymentRequests: [], products: [] } }
    }
    if (result.data !== null && products.data !== null) {
        return {
            props: { paymentRequests: result.data, products: products.data.data.products.edges }
        }
    }
}

export default AcceptPayment
